bool_t
xdr_startrep (XDR *xdrs, startrep *objp)
{
	register int32_t *buf;


	if (xdrs->x_op == XDR_ENCODE) {
		 if (!xdr_char (xdrs, &objp->towhat))
			 return FALSE;
		 if (!xdr_pointer (xdrs, (char **)&objp->s1, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
		buf = XDR_INLINE (xdrs, 5 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->with_page_length))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_left_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_right_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_top_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_bottom_margin))
				 return FALSE;

		} else {
		IXDR_PUT_LONG(buf, objp->with_page_length);
		IXDR_PUT_LONG(buf, objp->with_left_margin);
		IXDR_PUT_LONG(buf, objp->with_right_margin);
		IXDR_PUT_LONG(buf, objp->with_top_margin);
		IXDR_PUT_LONG(buf, objp->with_bottom_margin);
		}
		 if (!xdr_str (xdrs, &objp->with_top_of_page))
			 return FALSE;
		return TRUE;
	} else if (xdrs->x_op == XDR_DECODE) {
		 if (!xdr_char (xdrs, &objp->towhat))
			 return FALSE;
		 if (!xdr_pointer (xdrs, (char **)&objp->s1, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
		buf = XDR_INLINE (xdrs, 5 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->with_page_length))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_left_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_right_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_top_margin))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->with_bottom_margin))
				 return FALSE;

		} else {
		objp->with_page_length = IXDR_GET_LONG(buf);
		objp->with_left_margin = IXDR_GET_LONG(buf);
		objp->with_right_margin = IXDR_GET_LONG(buf);
		objp->with_top_margin = IXDR_GET_LONG(buf);
		objp->with_bottom_margin = IXDR_GET_LONG(buf);
		}
		 if (!xdr_str (xdrs, &objp->with_top_of_page))
			 return FALSE;
	 return TRUE;
	}

	 if (!xdr_char (xdrs, &objp->towhat))
		 return FALSE;
	 if (!xdr_pointer (xdrs, (char **)&objp->s1, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->with_page_length))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->with_left_margin))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->with_right_margin))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->with_top_margin))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->with_bottom_margin))
		 return FALSE;
	 if (!xdr_str (xdrs, &objp->with_top_of_page))
		 return FALSE;
	return TRUE;
}

