bool_t
xdr_event_data (XDR *xdrs, event_data *objp)
{
	register int32_t *buf;

	 if (!xdr_e_event (xdrs, &objp->event_type))
		 return FALSE;
	switch (objp->event_type) {
	case EVENT_BEF_ROW:
		break;
	case EVENT_AFT_ROW:
		break;
	case EVENT_BEFORE_DELETE:
		break;
	case EVENT_AFTER_DELETE:
		break;
	case EVENT_BEFORE_INSERT:
		break;
	case EVENT_AFTER_INSERT:
		break;
	case EVENT_BEFORE_OPEN_FORM:
		break;
	case EVENT_BEFORE_INSERT_DELETE:
		break;
	case EVENT_AFTER_INSERT_DELETE:
		break;
	case EVENT_AFTER_INP_CLEAN:
		break;
	case EVENT_ANYKEY_PRESS:
		break;
	case EVENT_ON_IDLE:
		 if (!xdr_int (xdrs, &objp->event_data_u.idle_n))
			 return FALSE;
		break;
	case EVENT_ON_INTERVAL:
		 if (!xdr_int (xdrs, &objp->event_data_u.interval_n))
			 return FALSE;
		break;
	case EVENT_ON_TIME:
		 if (!xdr_int (xdrs, &objp->event_data_u.time_n))
			 return FALSE;
		break;
	case EVENT_KEY_PRESS:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.key, sizeof (str_list), (xdrproc_t) xdr_str_list))
			 return FALSE;
		break;
	case EVENT_ON_ACTION:
		 if (!xdr_str (xdrs, &objp->event_data_u.on_action))
			 return FALSE;
		break;
	case EVENT_ON:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.on, sizeof (str_list), (xdrproc_t) xdr_str_list))
			 return FALSE;
		break;
	case EVENT_AFTER_INPUT:
		break;
	case EVENT_BEFORE_INPUT:
		break;
	case EVENT_AFTER_DISPLAY:
		break;
	case EVENT_BEFORE_DISPLAY:
		break;
	case EVENT_AFTER_CONSTRUCT:
		break;
	case EVENT_BEFORE_CONSTRUCT:
		break;
	case EVENT_BEFORE_MENU:
		break;
	case EVENT_BEFORE_EVENT:
		break;
	case EVENT_BEFORE_FIELD:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.before_field, sizeof (struct fh_field_list), (xdrproc_t) xdr_fh_field_list))
			 return FALSE;
		break;
	case EVENT_ON_CHANGE:
	case EVENT_AFTER_FIELD:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.after_field, sizeof (struct fh_field_list), (xdrproc_t) xdr_fh_field_list))
			 return FALSE;
		break;
	case EVENT_MENU_COMMAND:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.mnoption, sizeof (menuoption), (xdrproc_t) xdr_menuoption))
			 return FALSE;
		break;
	case EVENT_AFTER_EVENT:
		break;
	case EVENT_BEFORE_CLOSE_FORM:
		break;
	case EVENT_BEFORE_ANY:
		break;
	case EVENT_AFTER_ANY:
		break;
	case EVENT_BEFORE:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.before, sizeof (str_list), (xdrproc_t) xdr_str_list))
			 return FALSE;
		break;
	case EVENT_AFTER:
		 if (!xdr_pointer (xdrs, (char **)&objp->event_data_u.after, sizeof (str_list), (xdrproc_t) xdr_str_list))
			 return FALSE;
		break;
	default:
		return FALSE;
	}
	return TRUE;
}

