bool_t
xdr_simple_variable (XDR *xdrs, simple_variable *objp)
{
	register int32_t *buf;

	int i;

	if (xdrs->x_op == XDR_ENCODE) {
		buf = XDR_INLINE (xdrs, (1 + ( 2 )) * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->datatype))
				 return FALSE;
			 if (!xdr_vector (xdrs, (char *)objp->dimensions, 2,
				sizeof (int), (xdrproc_t) xdr_int))
				 return FALSE;

		} else {
		IXDR_PUT_LONG(buf, objp->datatype);
		{
			register int *genp;

			for (i = 0, genp = objp->dimensions;
				i < 2; ++i) {
				IXDR_PUT_LONG(buf, *genp++);
			}
		}
		}
		 if (!xdr_pointer (xdrs, (char **)&objp->defaultvalue, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
		return TRUE;
	} else if (xdrs->x_op == XDR_DECODE) {
		buf = XDR_INLINE (xdrs, (1 + ( 2 )) * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->datatype))
				 return FALSE;
			 if (!xdr_vector (xdrs, (char *)objp->dimensions, 2,
				sizeof (int), (xdrproc_t) xdr_int))
				 return FALSE;

		} else {
		objp->datatype = IXDR_GET_LONG(buf);
		{
			register int *genp;

			for (i = 0, genp = objp->dimensions;
				i < 2; ++i) {
				*genp++ = IXDR_GET_LONG(buf);
			}
		}
		}
		 if (!xdr_pointer (xdrs, (char **)&objp->defaultvalue, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
	 return TRUE;
	}

	 if (!xdr_int (xdrs, &objp->datatype))
		 return FALSE;
	 if (!xdr_vector (xdrs, (char *)objp->dimensions, 2,
		sizeof (int), (xdrproc_t) xdr_int))
		 return FALSE;
	 if (!xdr_pointer (xdrs, (char **)&objp->defaultvalue, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
		 return FALSE;
	return TRUE;
}

