		 return FALSE;
	 if (!xdr_int (xdrs, &objp->is_extern))
		 return FALSE;
	 if (!xdr_array (xdrs, (char **)&objp->arr_subscripts.arr_subscripts_val, (u_int *) &objp->arr_subscripts.arr_subscripts_len, ~0,
		sizeof (int), (xdrproc_t) xdr_int))
		 return FALSE;
