bool_t
xdr_fgl_comment (XDR *xdrs, fgl_comment *objp)
{
	register int32_t *buf;


	if (xdrs->x_op == XDR_ENCODE) {
		buf = XDR_INLINE (xdrs, 3 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->lineno))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->colno))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->printed))
				 return FALSE;

		} else {
		IXDR_PUT_LONG(buf, objp->lineno);
		IXDR_PUT_LONG(buf, objp->colno);
		IXDR_PUT_LONG(buf, objp->printed);
		}
		 if (!xdr_str (xdrs, &objp->comment))
			 return FALSE;
		 if (!xdr_char (xdrs, &objp->type))
			 return FALSE;
		return TRUE;
	} else if (xdrs->x_op == XDR_DECODE) {
		buf = XDR_INLINE (xdrs, 3 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->lineno))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->colno))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->printed))
				 return FALSE;

		} else {
		objp->lineno = IXDR_GET_LONG(buf);
		objp->colno = IXDR_GET_LONG(buf);
		objp->printed = IXDR_GET_LONG(buf);
		}
		 if (!xdr_str (xdrs, &objp->comment))
			 return FALSE;
		 if (!xdr_char (xdrs, &objp->type))
			 return FALSE;
	 return TRUE;
	}

	 if (!xdr_int (xdrs, &objp->lineno))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->colno))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->printed))
		 return FALSE;
	 if (!xdr_str (xdrs, &objp->comment))
		 return FALSE;
	 if (!xdr_char (xdrs, &objp->type))
		 return FALSE;
	return TRUE;
}

