bool_t
xdr_s_expr_cast (XDR *xdrs, s_expr_cast *objp)
{
	register int32_t *buf;


	if (xdrs->x_op == XDR_ENCODE) {
		 if (!xdr_pointer (xdrs, (char **)&objp->expr, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
		buf = XDR_INLINE (xdrs, 4 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->target_dtype))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->src_dtype))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->notnull))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->force))
				 return FALSE;
		} else {
			IXDR_PUT_LONG(buf, objp->target_dtype);
			IXDR_PUT_LONG(buf, objp->src_dtype);
			IXDR_PUT_LONG(buf, objp->notnull);
			IXDR_PUT_LONG(buf, objp->force);
		}
		return TRUE;
	} else if (xdrs->x_op == XDR_DECODE) {
		 if (!xdr_pointer (xdrs, (char **)&objp->expr, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
			 return FALSE;
		buf = XDR_INLINE (xdrs, 4 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->target_dtype))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->src_dtype))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->notnull))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->force))
				 return FALSE;
		} else {
			objp->target_dtype = IXDR_GET_LONG(buf);
			objp->src_dtype = IXDR_GET_LONG(buf);
			objp->notnull = IXDR_GET_LONG(buf);
			objp->force = IXDR_GET_LONG(buf);
		}
	 return TRUE;
	}

	 if (!xdr_pointer (xdrs, (char **)&objp->expr, sizeof (struct expr_str), (xdrproc_t) xdr_expr_str))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->target_dtype))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->src_dtype))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->notnull))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->force))
		 return FALSE;
	return TRUE;
}

