bool_t
xdr_ilist (XDR *xdrs, ilist *objp)
{
	register int32_t *buf;


	if (xdrs->x_op == XDR_ENCODE) {
		buf = XDR_INLINE (xdrs, 6 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->i0))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i1))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i2))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i3))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i4))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i5))
				 return FALSE;
		} else {
			IXDR_PUT_LONG(buf, objp->i0);
			IXDR_PUT_LONG(buf, objp->i1);
			IXDR_PUT_LONG(buf, objp->i2);
			IXDR_PUT_LONG(buf, objp->i3);
			IXDR_PUT_LONG(buf, objp->i4);
			IXDR_PUT_LONG(buf, objp->i5);
		}
		return TRUE;
	} else if (xdrs->x_op == XDR_DECODE) {
		buf = XDR_INLINE (xdrs, 6 * BYTES_PER_XDR_UNIT);
		if (buf == NULL) {
			 if (!xdr_int (xdrs, &objp->i0))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i1))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i2))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i3))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i4))
				 return FALSE;
			 if (!xdr_int (xdrs, &objp->i5))
				 return FALSE;
		} else {
			objp->i0 = IXDR_GET_LONG(buf);
			objp->i1 = IXDR_GET_LONG(buf);
			objp->i2 = IXDR_GET_LONG(buf);
			objp->i3 = IXDR_GET_LONG(buf);
			objp->i4 = IXDR_GET_LONG(buf);
			objp->i5 = IXDR_GET_LONG(buf);
		}
	 return TRUE;
	}

	 if (!xdr_int (xdrs, &objp->i0))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->i1))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->i2))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->i3))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->i4))
		 return FALSE;
	 if (!xdr_int (xdrs, &objp->i5))
		 return FALSE;
	return TRUE;
}

