bool_t
xdr_module_entry (XDR *xdrs, module_entry *objp)
{
	register int32_t *buf;

	 if (!xdr_module_entry_type (xdrs, &objp->met_type))
		 return FALSE;
	switch (objp->met_type) {
	case E_MET_IMPORT_FUNCTION_DEFINITION:
		 if (!xdr_s_import_function_definition (xdrs, &objp->module_entry_u.import_function_definition))
			 return FALSE;
		break;
	case E_MET_IMPORT_LEGACY_DEFINITION:
		 if (!xdr_s_import_legacy_definition (xdrs, &objp->module_entry_u.import_legacy_definition))
			 return FALSE;
		break;
	case E_MET_IMPORT_DATATYPE:
		 if (!xdr_s_import_datatype (xdrs, &objp->module_entry_u.import_datatype_definition))
			 return FALSE;
		break;
	case E_MET_IMPORT_PACKAGE:
		 if (!xdr_s_import_package (xdrs, &objp->module_entry_u.import_package_definition))
			 return FALSE;
		break;
	case E_MET_MAIN_DEFINITION:
	case E_MET_FUNCTION_DEFINITION:
		 if (!xdr_s_function_definition (xdrs, &objp->module_entry_u.function_definition))
			 return FALSE;
		break;
	case E_MET_FORMHANDLER_DEFINITION:
		 if (!xdr_s_formhandler_definition (xdrs, &objp->module_entry_u.formhandler_definition))
			 return FALSE;
		break;
	case E_MET_REPORT_DEFINITION:
		 if (!xdr_s_report_definition (xdrs, &objp->module_entry_u.report_definition))
			 return FALSE;
		break;
	case E_MET_PDF_REPORT_DEFINITION:
		 if (!xdr_s_pdf_report_definition (xdrs, &objp->module_entry_u.pdf_report_definition))
			 return FALSE;
		break;
	case E_MET_CLASS_DEFINITION:
		 if (!xdr_s_class_definition (xdrs, &objp->module_entry_u.class_definition))
			 return FALSE;
		break;
	case E_MET_CMD:
		 if (!xdr_pointer (xdrs, (char **)&objp->module_entry_u.cmd, sizeof (command), (xdrproc_t) xdr_command))
			 return FALSE;
		break;
	default:
		return FALSE;
	}
	return TRUE;
}

